(265, 1, '2013-07-08 09:05:36', '2013-07-08 09:05:36', '<br></br>\r\n<h2 style="text-align: left;"><span style="text-decoration: underline;">Security Sense Quiz</span></h2>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=180">Spring Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=170">Privacy Awareness Week Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=186">Fraud Month – Quiz 1</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=192">Fraud Month – Quiz 2</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=163">Encryption Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=178">Cyber Security Quiz</a></div>\r\n<div id="c1362865926066">\r\n\r\n&nbsp;\r\n<h2><span style="text-decoration: underline;"><a href="http://www.cio.gov.bc.ca/cio/informationsecurity/isawareness/past_quizzes.page">British Columbia Quiz - 2013</a></span></h2>\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/April2013Quiz/April2013Quiz.htm" target="_blank">Spring Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/paw-apr2013/paw-apr2013.htm" target="_blank">Privacy Awareness Week Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/FraudMonthQuiz/FraudMonth.htm" target="_blank">Fraud Month - Quiz 1</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/FraudMonthQuiz/FraudMonth2.htm" target="_blank">Fraud Month - Quiz 2</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/JanuaryQuiz/JanuaryQuiz.htm" target="_blank">Encryption Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/CyberSecurityMonth/CyberSecurityQuiz/CyberSecurityQuiz.htm">Cyber Security Quiz</a>\r\n\r\n</div>', 'INFORMATION SECURITY AWARENESS QUIZZES', '', 'inherit', 'open', 'open', '', '115-revision-v1', '', '', '2013-07-08 09:05:36', '2013-07-08 09:05:36', '', 115, 'http://localhost/wordpress/?p=265', 0, 'revision', '', 0),
(101, 1, '2013-07-02 10:25:48', '2013-07-02 10:25:48', '<a title="Security Awareness" href="https://www.myvalleybank.com/security-awareness.htm">security-awareness</a>\r\n\r\nVisit any of the links below for more Security Awareness Information:\r\n<ul>\r\n	<li><img alt="" src="https://www.myvalleybank.com/images/Security_Awareness.jpg" width="280" height="183" align="right" border="1" /><a href="https://www.myvalleybank.com/files/Fraudulent_Emails__Websites_and_Phishing_Variations.pdf" target="_self">Fraudulent Emails, Websites and Phishing Variations</a></li>\r\n	<li><a href="https://www.myvalleybank.com/security-awareness-fraud-id-theft-services.htm" target="_self">Fraud &amp; Identity Theft Services</a></li>\r\n	<li><a href="https://www.myvalleybank.com/security-awareness-types-id-theft.htm" target="_self">Types of Identity Theft</a></li>\r\n	<li><a href="https://www.myvalleybank.com/security-awareness-tips-avoid-id-theft.htm" target="_self">Tips to Avoid Identity Theft</a></li>\r\n	<li><a href="https://www.myvalleybank.com/security-awareness-credit-reports.htm" target="_self">Credit Reports</a></li>\r\n	<li><a href="https://www.myvalleybank.com/security-awareness-what-should-i-do.htm" target="_self">What Should I Do?</a></li>\r\n	<li><a href="https://www.myvalleybank.com/protect-online.htm" target="_self">Internet Security Tips</a></li>\r\n	<li><a href="https://www.myvalleybank.com/security-awareness-security-freeze.htm" target="_self">Security Freeze</a></li>\r\n	<li><a href="https://www.myvalleybank.com/files/OLB_Cash_Management_Fraud_Considerations_.pdf" target="_blank">OLB Cash Management Fraud Considerations</a></li>\r\n	<li><a href="https://www.myvalleybank.com/files/OLB_Fraud_Considerations_-_Retail.pdf" target="_blank">OLB Retail Fraud Considerations</a></li>\r\n</ul>', 'Security Awareness', '', 'publish', 'open', 'open', '', 'security-awareness', '', '', '2013-07-02 10:43:03', '2013-07-02 10:43:03', '', 0, 'http://localhost/wordpress/?page_id=101', 0, 'page', '', 0),
