(183, 1, '2013-07-03 03:11:59', '2013-07-03 03:11:59', '<h2 style="text-align: left;"><span style="text-decoration: underline;">Security Sense Quiz</span></h2>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=180">Spring Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=170">Privacy Awareness Week Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=163">Encryption Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=178">Cyber Security Quiz</a></div>\r\n<div id="c1362865926066">\r\n\r\n&nbsp;\r\n<h2><span style="text-decoration: underline;"><a href="http://www.cio.gov.bc.ca/cio/informationsecurity/isawareness/past_quizzes.page">British Columbia Quiz - 2013</a></span></h2>\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/April2013Quiz/April2013Quiz.htm" target="_blank">Spring Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/paw-apr2013/paw-apr2013.htm" target="_blank">Privacy Awareness Week Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/FraudMonthQuiz/FraudMonth.htm" target="_blank">Fraud Month - Quiz 1</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/FraudMonthQuiz/FraudMonth2.htm" target="_blank">Fraud Month - Quiz 2</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/JanuaryQuiz/JanuaryQuiz.htm" target="_blank">Encryption Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/CyberSecurityMonth/CyberSecurityQuiz/CyberSecurityQuiz.htm">Cyber Security Quiz</a>\r\n\r\n</div>', 'INFORMATION SECURITY AWARENESS QUIZZES', '', 'inherit', 'open', 'open', '', '115-revision-v1', '', '', '2013-07-03 03:11:59', '2013-07-03 03:11:59', '', 115, 'http://localhost/wordpress/?p=183', 0, 'revision', '', 0),
(184, 1, '2013-07-03 03:12:30', '2013-07-03 03:12:30', '<h2 style="text-align: left;"></h2>\r\n<h2 style="text-align: left;"><span style="text-decoration: underline;">Security Sense Quiz</span></h2>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=180">Spring Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=170">Privacy Awareness Week Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=163">Encryption Quiz</a></div>\r\n<div id="simpleQuiz"><a href="http://localhost/wordpress/?page_id=178">Cyber Security Quiz</a></div>\r\n<div id="c1362865926066">\r\n\r\n&nbsp;\r\n<h2><span style="text-decoration: underline;"><a href="http://www.cio.gov.bc.ca/cio/informationsecurity/isawareness/past_quizzes.page">British Columbia Quiz - 2013</a></span></h2>\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/April2013Quiz/April2013Quiz.htm" target="_blank">Spring Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/paw-apr2013/paw-apr2013.htm" target="_blank">Privacy Awareness Week Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/FraudMonthQuiz/FraudMonth.htm" target="_blank">Fraud Month - Quiz 1</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/FraudMonthQuiz/FraudMonth2.htm" target="_blank">Fraud Month - Quiz 2</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/JanuaryQuiz/JanuaryQuiz.htm" target="_blank">Encryption Quiz</a>\r\n\r\n<a href="http://www.cio.gov.bc.ca/local/cio/informationsecurity/CyberSecurityMonth/CyberSecurityQuiz/CyberSecurityQuiz.htm">Cyber Security Quiz</a>\r\n\r\n</div>', 'INFORMATION SECURITY AWARENESS QUIZZES', '', 'inherit', 'open', 'open', '', '115-revision-v1', '', '', '2013-07-03 03:12:30', '2013-07-03 03:12:30', '', 115, 'http://localhost/wordpress/?p=184', 0, 'revision', '', 0),
