INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(422, 1, '2013-08-28 13:57:43', '2013-08-28 13:57:43', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="497" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="36" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="36">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9">3</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 4</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 5</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 6</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 7</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 8</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 9</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 10</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 11</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 12</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 13</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 14</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 15</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"> 16</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 17</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 18</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 19</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 20</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 21</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 22</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 23</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 24</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 25</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 26</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 27</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"> 28</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"> 29</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"> 30</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 31</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 32</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 33</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 34</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 35</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"> 36</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 37</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 38</td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 39</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 40</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"> 41</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"> 42</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"> 43</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 44</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 45</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"> 46</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"> 47</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"> 48</td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="36"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'publish', 'open', 'open', '', 'practical-security-framework-sample-policy-and-procedures', '', '', '2013-08-28 14:12:50', '2013-08-28 14:12:50', '', 0, 'http://localhost/wordpress/?page_id=422', 0, 'page', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(425, 1, '2013-08-28 13:59:28', '2013-08-28 13:59:28', '<style type="text/css"><!--\r\n@page { margin: 0.79in }\r\n		P { margin-bottom: 0.08in }\r\n		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="780" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="136" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="136">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"></td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 13:59:28', '2013-08-28 13:59:28', '', 422, 'http://localhost/wordpress/?p=425', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(423, 1, '2013-08-28 13:57:43', '2013-08-28 13:57:43', '<style type="text/css"><!--\r\n@page { margin: 0.79in }\r\n		P { margin-bottom: 0.08in }\r\n		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="780" cellspacing="2" cellpadding="0"><colgroup><col width="36" /> <col width="152" /> <col width="444" /> <col width="136" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="136">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span>\r\n\r\n<span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span>\r\n\r\n<span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span>\r\n\r\n<span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span>\r\n\r\n<span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"></td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY">\r\n\r\n</p>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 13:57:43', '2013-08-28 13:57:43', '', 422, 'http://localhost/wordpress/?p=423', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(424, 1, '2013-08-28 14:13:32', '2013-08-28 14:13:32', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="497" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="36" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="36">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9">3</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 4</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 5</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 6</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 7</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 8</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 9</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 10</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 11</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 12</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 13</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 14</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 15</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"> 16</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 17</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 18</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 19</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 20</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 21</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 22</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 23</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 24</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 25</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 26</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 27</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"> 28</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"> 29</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"> 30</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 31</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 32</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 33</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 34</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 35</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"> 36</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 37</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 38</td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 39</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 40</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"> 41</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"> 42</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"> 43</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 44</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 45</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"> 46</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"> 47</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"> 48</td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="36"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-autosave-v1', '', '', '2013-08-28 14:13:32', '2013-08-28 14:13:32', '', 422, 'http://localhost/wordpress/?p=424', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(426, 1, '2013-08-28 14:02:46', '2013-08-28 14:02:46', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="780" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="136" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="136">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"></td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 14:02:46', '2013-08-28 14:02:46', '', 422, 'http://localhost/wordpress/?p=426', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(427, 1, '2013-08-28 14:04:19', '2013-08-28 14:04:19', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="502" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="136" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="136">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"></td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"></td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"></td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 14:04:19', '2013-08-28 14:04:19', '', 422, 'http://localhost/wordpress/?p=427', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(428, 1, '2013-08-28 14:06:02', '2013-08-28 14:06:02', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="502" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="136" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="136">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9">3</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 4</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 5</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 6</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 7</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 8</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 9</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 10</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 11</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 12</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 13</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 14</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 15</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"> 16</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 17</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 18</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 19</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 20</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 21</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 22</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 23</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 24</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 25</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 26</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 27</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"> 28</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"> 29</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"> 30</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 31</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 32</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 33</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 34</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 35</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"> 36</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 37</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 38</td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 39</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 40</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"> 41</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"> 42</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"> 43</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 44</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 45</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"> 46</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"> 47</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"> 48</td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 14:06:02', '2013-08-28 14:06:02', '', 422, 'http://localhost/wordpress/?p=428', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(429, 1, '2013-08-28 14:11:21', '2013-08-28 14:11:21', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="497" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="36" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="136">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9">3</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 4</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 5</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 6</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 7</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 8</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 9</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 10</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 11</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 12</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 13</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 14</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 15</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"> 16</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 17</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 18</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 19</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 20</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 21</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 22</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 23</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 24</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 25</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 26</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 27</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"> 28</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"> 29</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"> 30</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 31</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 32</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 33</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 34</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 35</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"> 36</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 37</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 38</td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 39</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 40</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"> 41</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"> 42</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"> 43</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 44</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 45</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"> 46</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"> 47</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"> 48</td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 14:11:21', '2013-08-28 14:11:21', '', 422, 'http://localhost/wordpress/?p=429', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(430, 1, '2013-08-28 14:11:50', '2013-08-28 14:11:50', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="497" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="36" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="36">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9">3</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 4</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 5</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 6</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 7</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 8</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 9</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 10</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 11</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 12</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 13</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 14</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 15</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"> 16</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 17</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 18</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 19</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 20</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 21</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 22</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 23</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 24</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 25</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 26</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 27</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"> 28</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"> 29</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"> 30</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 31</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 32</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 33</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 34</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 35</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"> 36</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 37</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 38</td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 39</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 40</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"> 41</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"> 42</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"> 43</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 44</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 45</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"> 46</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"> 47</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"> 48</td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 14:11:50', '2013-08-28 14:11:50', '', 422, 'http://localhost/wordpress/?p=430', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(431, 1, '2013-08-28 14:12:50', '2013-08-28 14:12:50', '<style type="text/css"><!--\r\n@page { margin: 0.79in } 		P { margin-bottom: 0.08in } 		A:link { so-language: zxx }\r\n--></style>\r\n<p align="JUSTIFY"><span style="font-size: small;">Following policy/procedure/template will be used as part of this security framework to ensure to the integrity, confidentiality and availability of resources of Securitysense. </span></p>\r\n\r\n<table width="497" border="5" cellspacing="2" cellpadding="0"><colgroup> <col width="36" /> <col width="152" /> <col width="444" /> <col width="36" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width="36" height="6">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">SL</span></span></span></p>\r\n</td>\r\n<td width="152">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Policy/Procedure</span></span></span></p>\r\n</td>\r\n<td width="444">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Purpose</span></span></span></p>\r\n</td>\r\n<td width="36">\r\n<p align="CENTER"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Target Employee</span></span></span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="53"> 1</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Acceptable%20Use%20Policy%20Ver2%200.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Acceptable Use Policy Ver2.0</span></span></span></a></td>\r\n<td width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to outline the acceptable use of computer equipment at Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 2</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Access Control Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document defines the access control policy required to securely deploy, manage and control user network, operating system, application and database access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-size: small;">Group of Employee.</span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System/Application Owner.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9">3</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Anti%20Virus%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Antivirus policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">To establish requirements which must be met by all computers connected to Securitysense’s networks to ensure effective virus detection and prevention.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 4</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Backup policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy establishes a backup strategy to address different types of service disruptions; minimal backup frequencies; recovery point objectives; and establishes the roles of various Staff positions having responsibilities relating to backup and recovery of critical systems and services.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 5</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Data Centre Access Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines how Securitysense individuals will conduct themselves within the Datacenter.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 6</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy states the requirements for securely storing and retrieving database usernames and passwords (i.e., database credentials) for use by a program that will access a database as well as overall database administration security for any Database running on of Securitysense’s networks.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Database Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 7</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Email%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">E-mail Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the policy is to ensure that we use the email system in an efficient but at the same time careful way when we communicate.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 8</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Encryption%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Encryption Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidance that limits the use of encryption to those algorithms that have received substantial public review and have been proven to work effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 9</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Protection%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Protection Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Protection Policy aims at establishing a common understanding and approach to information security so to ensure better compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 10</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/IT%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">IT Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this IT policy is to address all issues relevant to standardization of hardware equipment and application software, software installation, deployment, access and security of IT/Technology resources in Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 11</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Classification%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Classification Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The Information Classification Policy is intended to help employees determine what information can be disclosed to non-employees, as well as the relative sensitivity of information that should not be disclosed outside of Securitysense Axiata Limited without proper authorization.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 12</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet DMZ Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to define standards to be met by all equipment owned and/or operated by RAx located outside RAx''s corporate Internet firewalls.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 13</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Internet%20Usage%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internet Usage Policy provides employees with rules and guidelines about the appropriate use of company equipment, network and Internet access.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 14</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Intrusion Detection Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">It provides guidelines about intrusion detection implementation of the organizational networks and hosts along with associated roles and responsibilities.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 15</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Log monitoring Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy reflects the Securitysense’s commitment to identify and implement security controls to keep risks to information system resources at reasonable and appropriate levels.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="46"> 16</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Media%20Disposal%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Media Disposal Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy defines the steps necessary to implement Securitysense’s information system &amp; media disposal.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 17</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/End%20User%20Password%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">End User Password Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish a standard for creation of strong passwords, the protection of those passwords, and the frequency of change.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 18</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Patch Management Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will guide Windows OS patch update for Windows 2003 Enterprise Server R2</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Windows Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 19</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Personal%20Computer%20Security%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Personal Computer Security Policy Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal Desktop equipment (PC/Laptop/Printer) that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 20</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Remote%20Access%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Remote Access Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this Policy is to define standards for connecting to Securitysense’s network from any remote host, untrusted host, and remote network, including untrusted hosts on Securitysense’s intranet.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 21</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Treatment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risks Treatment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy acknowledges that once risks are identified they must be treated.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 22</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Assesment%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Assessment Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy stresses the importance of conducting risk assessments on Information Resources.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 23</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Router Security Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document describes a required minimal security configuration for all routers, switches and others perimeter devices connecting to a production network or used in a production capacity at or on behalf of Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 24</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Server Security Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to establish standards for the base configuration of internal server equipment that is owned and/or operated by Securitysense.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employee</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 25</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Access%20Control%20Policy%20for%20Third%20Party%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Access Control Policy for Third Party Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The aim of this third party access security policy is to allow the Company to exploit the business benefits of third party access in a secure manner and to manage the associated risks effectively.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 26</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Virtual%20Private%20Network(VPN)%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Virtual Private Network (VPN) Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to provide guidelines for Remote Access IPSec or L2TP Virtual Private Network (VPN) connections to the Securitysense Axiata Limited corporate network.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">VPN User Group.</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="65"> 27</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Wireless%20Communication%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Wireless Communication Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy describes how wireless network communication technologies are to be deployed, administered, and supported at Securitysense Axiata Limited.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="64"> 28</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/General%20&amp;%20Miscellanious%20Policy%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">General &amp; Miscellaneous Policy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This will give idea General rules for handling IT resources</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="120"> 29</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Software%20Aquisition%20Policy%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Software Acquisition Policy Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to outline the steps that RAx need to take in order to ensure that all purchase software is processed into inventory tracking process/system and all documentation of ownership is properly stored and maintained and copy right low follows.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="141"> 30</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/File%20Server%20Policy%20Ver1.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">File Server Policy Ver1.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this policy is to protect the company’s information/data from all threat whether internal or external, deliberate or accidental. The file server policy aims at establishing a common understanding and approach to file server so to ensure better data/file management, compliance by all concerned parties.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 31</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Internal Audit (ISMS) Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This document provides guidelines and procedures for internal audit of ISMS.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 32</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Corrective and Preventive Action Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Provides detailed procedures for implementation of preventive and corrective actions for information security incident or any non-conformance encountered during internal audit / external audit process</span></span></span><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 33</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Protection and Control Document Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This is the procedure, identified in section 5.2 of the Securitysense Axiata Limited’s information security framework/ policy, for defining how the Securitysense Axiata Limited protects and controls the documentsrequired by the ISMS. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Technology Audit Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="60"> 34</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Change Management Process and Strategy Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-size: small;">The </span></span><span style="color: #000000;"><span style="font-size: small;">purpose of this procedure is to define how the Securitysense Axiata Limited protects and controls the documents required by the ISMS.</span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 35</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Incident%20Handling%20Procedure%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Incident Handling Procedure Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The document provides some general guidelines and procedures for dealing with IT / Technology incidents.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="99"> 36</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/Information%20Security%20Risk%20Management%20Methodology%20Ver2.0.pdf"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Risk Management Methodology Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of the risk assessment is to </span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">identify all susceptibilities to the identified threats, and the nature of the harm that could arise from them.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 37</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Procedure for Router Security Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Essential security configuration for router.</span></span></span></td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="67"> 38</td>\r\n<td width="152">\r\n<p align="JUSTIFY"><a href="http://infosec.robi.com.bd/images/stories/final%20policy/System%20Classification%20Procedure%20Ver2.0.pdf"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System Classifica</span></span><span style="font-size: small;">t</span><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ion Procedure Ver2.0</span></span></a></p>\r\n</td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to ease and standardize the process of classifying information systems.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="82"> 39</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">System development Life Cycle Ver2.0</span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The SDLC serves as the mechanism to assure that systems under development meet the established requirements and support the Securitysense Axiata Limited business functions.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Development Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 40</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guideline for Project Ver2.0 </span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of these guidelines is to ensure that appropriate level of information security control is in place for new business initiatives/project.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Project Team.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="69"> 41</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Windows Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This Domain Windows Server Baseline Security (WSBS) Implementation Guide is intended to provide configuration guidance</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="81"> 42</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for UNIX/LINUX Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational UNIX/LINUX environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="75"> 43</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Baseline Security Guide for Database Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this document is to obtain a direction for creation and maintenance of a secure operational database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 44</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Core Site Physical Access Guidelines Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">This policy aims at providing protection of all Securitysense Axiata Limited assets, business processes and employees from external and internal theft, accidental or malicious damage,\r\nsabotage, subversion, leakage and other natural threats by establishing cost effective security solutions</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="9"> 45</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">BTS Sites Physical Access Guidelines Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to establish standards for securing Securitysense Base Transceiver Station (BTS) sites which contains the equipment for transmitting and receiving radio\r\nsignals (transceivers), antennas, and equipment for encrypting and decrypting communications with the base station controller (BSC).</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees</span></span></span><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;"><b>.</b></span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="71"> 46</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for MySQL Database Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to obtain a direction for creation and maintenance of a secure operational MySQL database environment within Securitysense service.</span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="39"> 47</td>\r\n<td width="152"><a href="http://infosec.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Security Guide for Web Server Ver1.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #000000;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">The purpose of this guideline is to ensure that basic security safeguards are utilized. </span></span></span></p>\r\n</td>\r\n<td width="136"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Authorized Employees.</span></span></span></td>\r\n</tr>\r\n<tr>\r\n<td width="36" height="8"> 48</td>\r\n<td width="152"><a href="http://securitysense.robi.com.bd/index.php?option=com_content&amp;view=article&amp;id=110&amp;Itemid=108"><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awar</span></span></span><span style="color: #0000ff;"><span style="font-size: small;">e</span></span><span style="color: #0000ff;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">ness Manual Ver2.0</span></span></span></a></td>\r\n<td valign="TOP" width="444">\r\n<p align="JUSTIFY"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">Information Security Awareness</span></span></span></p>\r\n</td>\r\n<td width="36"><span style="color: #333333;"><span style="font-family: Axiata Book,serif;"><span style="font-size: small;">All Employees.</span></span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p align="JUSTIFY"></p>', 'Practical security framework sample : POLICY AND PROCEDURES', '', 'inherit', 'open', 'open', '', '422-revision-v1', '', '', '2013-08-28 14:12:50', '2013-08-28 14:12:50', '', 422, 'http://localhost/wordpress/?p=431', 0, 'revision', '', 0),
